import classNames from "classnames/bind";
import styles from "./Button.module.scss";
import { Link } from "react-router-dom";

const cx = classNames.bind(styles)

function Button({to , href , primary , text, outline = false , large = false, small = false, children , onClick , ...passProps}) {

    let Comp = 'button';
    const poprs = {
        onClick,
        ...passProps
    };

    if(to){
        poprs.to = to
        Comp = Link
    } else if(href){
        poprs.href = href
        Comp = 'a'
    }

    const classes = cx('wrapper' , {primary , outline, small , large} );

    return (
        <Comp className={classes} {...poprs}>
            <span>{children}</span>
        </Comp>
    )
}

export default Button
