import styles from "./Button.module.scss"

const cx = classNames.bind(styles)

function Button({to , href , primary, children , onClick}) {

    let Comp = 'button';
    const poprs = {
        onClick,
    };

    if(to){
        poprs.to = to
        Comp = Link
    } else {
        poprs.href = href
        Comp = "a"
    }

    const classes = cx('wrapper');

    return (
        <Comp className={classes} {...poprs}>
            <span>{children}</span>
        </Comp>
    )
}

export default Button
